func testCompanyToManyAddOpEmployees(t *testing.T) {
	var err error

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a Company
	var b, c, d, e Employee

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, companyDBTypes, false, strmangle.SetComplement(companyPrimaryKeyColumns, companyColumnsWithoutDefault)...); err != nil {
		t.Fatal(err)
	}
	foreigners := []*Employee{&b, &c, &d, &e}
	for _, x := range foreigners {
		if err = randomize.Struct(seed, x, employeeDBTypes, false, strmangle.SetComplement(employeePrimaryKeyColumns, employeeColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	foreignersSplitByInsertion := [][]*Employee{
		{&b, &c},
		{&d, &e},
	}

	for i, x := range foreignersSplitByInsertion {
		err = a.AddEmployees(ctx, tx, i != 0, x...)
		if err != nil {
			t.Fatal(err)
		}

		first := x[0]
		second := x[1]

		if a.ID != first.CompanyID {
			t.Error("foreign key was wrong value", a.ID, first.CompanyID)
		}
		if a.ID != second.CompanyID {
			t.Error("foreign key was wrong value", a.ID, second.CompanyID)
		}

		if first.R.Company != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}
		if second.R.Company != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}

		if a.R.Employees[i*2] != first {
			t.Error("relationship struct slice not set to correct value")
		}
		if a.R.Employees[i*2+1] != second {
			t.Error("relationship struct slice not set to correct value")
		}

		count, err := a.Employees().Count(ctx, tx)
		if err != nil {
			t.Fatal(err)
		}
		if want := int64((i + 1) * 2); count != want {
			t.Error("want", want, "got", count)
		}
	}
}
